import React, {Component} from "react";
import Sidebar from "./Sidebar";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'

class Navigation extends Component {

    menuIcon = 'faBars';

    toggleSidebar = () => {
        document.querySelector('.navigation__sidebar').classList.toggle('navigation__sidebar-open');
        if (document.querySelector('.navigation__sidebar').hasClass('navigation__sidebar-open')) {
            this.menuIcon = 'dadadsd';
        } else {
            this.menuIcon = 'faBars';
        }
    }

    render() {
        return (
            <nav className="navigation">
                <i onClick={this.toggleSidebar} className="fas fa-bars navigation__hamburger"></i>
                <FontAwesomeIcon icon={menuIcon} onClick={this.toggleSidebar} className="navigation__hamburger" />
                <Sidebar />
                <h1 className="navigation__title">Compare Movies Info Game</h1>
            </nav>
        );
    }
}

export default Navigation;
