import React, {Component} from "react";
import SingleCard from "./SingleCard";

class CardsContainer extends Component {

    state = {
        resultVisiblity: 'hidden',
        cardOne: this.props.cardOne,
        cardTwo: this.props.cardTwo
    }

    showResult = (event) => {
        this.setState({
            resultVisiblity: ''
        });
    }

    render() {
        return (
            <section className={"game-container-dojo__cards-container " + this.state.resultVisiblity}>
                <SingleCard number='one' click={this.showResult}>{'$' + this.state.cardOne }</SingleCard>
                <SingleCard number='two' click={this.showResult}>{'$' + this.state.cardTwo }</SingleCard>
            </section>
        );
    }
}

export default CardsContainer;
