import React, {Component} from "react";

class SingleCard extends Component {

    state = {
        visiblity: 'hidden'
    }

    showResult = () => {
        this.setState({
            visiblity: 'visible'
        });
    }

    render() {
        return (
            <div onClick={this.showResult} className="cards-container__card">
                <img src="https://image.tmdb.org/t/p/w300_and_h450_bestv2/6ApDtO7xaWAfPqfi2IARXIzj8QS.jpg" className="card__poster" />
                <div className="card__title">Ford v Ferrary</div>
                <div className={"card__result " + this.state.visiblity} >$52,437,000.00</div>
            </div>
        );
    }
}

export default SingleCard;
