import React, {Component} from 'react';
import Scoreboard from "./Scoreboard";
import CardsContainer from "./CardsContainer";
import SingleCard from "./SingleCard";

class GameContainer extends Component {

    state = {
        cardsResultsVisibility: 'hidden',
        gameStatus: 'gc-lose',
        cardOne: Math.floor(Math.random() * 200),
        cardTwo: Math.floor(Math.random() * 200),
        winner: null
    }

    showResult = (event) => {
        this.setState({
            cardsResultsVisibility: '',
            winner: (this.state.cardOne > this.state.cardTwo) ? this.state.cardOne : this.state.cardTwo,
        });
        if (this.state.winner === event.target.innerText) {
            console.log(event.target.innerText);
            console.log('WIN');
        } else {
            console.log(event.target.children[2]);
            console.log('LOSE');
        }
    }

    render() {

        const newArray = new Array(this.state.cardOne, this.state.cardTwo);
        console.log(newArray);

        return (
            <section className={"game-container " + this.state.gameStatus}>
                <div className="game-container__dojo">
                    <Scoreboard />
                    <CardsContainer visible={ this.state.cardsResultsVisibility }>
                        <SingleCard number='one' click={this.showResult}>{'$' + this.state.cardOne }</SingleCard>
                        <SingleCard number='two' click={this.showResult}>{'$' + this.state.cardTwo }</SingleCard>
                    </CardsContainer>
                </div>
            </section>
        );
    }
}

export default GameContainer;
